// Helper function to find officer for a complaint
export function findOfficer(zone: string, ward: string, complaintType: string): OfficerMapping | null {
    // Determine department from complaint type
    const department = getDepartmentFromComplaintType(complaintType);

    // Normalize inputs
    const cleanZone = zone.trim().toLowerCase();
    const cleanWard = ward.trim();

    // 1. Try Exact Match first (fast path)
    const exactMatch = officerMappings.find(
        mapping =>
            mapping.zone.toLowerCase() === cleanZone &&
            mapping.ward === cleanWard &&
            mapping.department === department
    );
    if (exactMatch) return exactMatch;

    // 2. Try Matching by Ward Number (Robust)
    // Extracts "30" from "30-Krishna Nagar" or "30 - Krishna Nagar"
    const wardNumberMatch = cleanWard.match(/^(\d+)/);

    if (wardNumberMatch) {
        const wardNum = parseInt(wardNumberMatch[1], 10);

        const numberMatch = officerMappings.find(mapping => {
            // Check department first
            if (mapping.department !== department) return false;

            // Extract number from mapping ward string "30-Krishna Nagar Second"
            const mappingWardMatch = mapping.ward.match(/^(\d+)/);
            if (!mappingWardMatch) return false;

            return parseInt(mappingWardMatch[1], 10) === wardNum;
        });

        if (numberMatch) return numberMatch;
    }

    // 3. Try loose name match (if number extraction failed or no match found)
    // Matches if mapping ward name contains input ward name or vice versa
    const nameMatch = officerMappings.find(mapping => {
        if (mapping.department !== department) return false;

        // Remove numbers and special chars for comparison
        const mName = mapping.ward.replace(/^[\d\-\s]+/, '').toLowerCase();
        const iName = cleanWard.replace(/^[\d\-\s]+/, '').toLowerCase();

        return mName.includes(iName) || iName.includes(mName);
    });

    return nameMatch || null;
}

