// Helper function to find officer for a complaint
export function findOfficer(zone: string, ward: string, complaintType: string): OfficerMapping | null {
    // Clean zone name (remove number prefix like "3-")
    const cleanZone = zone.replace(/^\d+-/, '').trim();

    // Determine department from complaint type
    const department = getDepartmentFromComplaintType(complaintType);

    // Find matching officer
    return officerMappings.find(
        mapping =>
            mapping.zone.toLowerCase() === cleanZone.toLowerCase() &&
            mapping.ward === ward &&
            mapping.department === department
    ) || null;
}

function getDepartmentFromComplaintType(type: string): string {
    const sanitationTypes = ['Door To Door', 'Road Sweeping', 'Drain Cleaning', 'Sanitation', 'Dead Animals'];
    const civilTypes = ['STREET LIGHT', 'Civil', 'Water Logging'];

    if (sanitationTypes.some(t => type?.includes(t))) return 'Sanitation';
    if (civilTypes.some(t => type?.includes(t))) return 'Civil';

    return 'Sanitation'; // Default
}
